INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (1, 'Samuel', 'Lamarche', 'Fondateur de L’Agenda, vice-président de PQLaval et étudiant en économie et politique à l’Université Laval. Né à Amos, en Abitibi, il est passionné de politique depuis son jeune âge et rêve de voir le Québec devenir un pays. Toutefois, son objectif premier avec L’Agenda reste d’informer les gens, de leur donner une voix et de les intéresser à la politique. Pour lui, le cynisme et le désintérêt en politique sont des indicateurs que la société est à la recherche d’un nouveau souffle et d’un nouveau projet. Il se qualifie comme étant pragmatique et peu enclin à tomber dans l’idéalisme et le romantisme de la politique.', 13, true, 'Animateur et rédacteur en chef', 10);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (2, 'Antoine', 'Poulin', '', 11, false, 'Ancien membre', null);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (3, 'Jordan', 'Larochelle', 'Collaborateur à L’Agenda, militant pour Québec solidaire et étudiant en science politique à l’Université du Québec en Outaouais. Né à Amos en Abitibi, il tombe en amour avec la politique à un très jeune âge. Défenseur de la justice sociale et soucieux de réduire les inégalités socio-économiques, il s’engage comme militant pour Québec Solidaire et le NPD au fédéral. Partisan d’une réforme du mode de scrutin, il juge que la population doit avoir un plus grand rôle dans les décisions de l’État et que c’est de cette manière que l’on viendra à bout du cynisme croissant de la population.', 12, true, 'Collaborateur', 30);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (4, 'Jonathan', 'Carreiro-Benoit', 'Cofondateur de L’Agenda, président de PQLaval et étudiant en histoire et études anciennes à l’Université Laval. Né à Cantley, en Outaouais, il se présente pour le PQ et le Bloc dans une région fortement hostile à l’indépendance. Sa passion pour la politique et l’histoire le motive à vouloir enseigner l’histoire, mais aussi à intéresser les gens à la joute qui se déroule aux parlements. Plus romantique et idéaliste, il voit dans la politique une bataille honorable pour faire avancer des idéaux et des visions bien souvent incompatibles. La défense du Québec et son accession à l’indépendance le motivent à se lever chaque jour pour changer un peu le monde à sa façon.', 6, true, 'Co-Fondateur', 20);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (5, 'Liam', 'Buell', '', 15, false, 'Lecteur', null);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (6, 'Jérémie', 'Juneau', 'Collaborateur à L’Agenda, candidat du NPD dans Louis-Hébert en 2019 et étudiant en comptabilité à l’Université Laval. Né à Amos, en Abitibi et fils d’entrepreneur, il se découvre un intérêt profond pour les PME et leur importance dans l’économie canadienne. Il se lance en politique avec le NPD avec une vision autonomiste pour le Canada. Ses priorités sont la fiscalité, la justice sociale, l’environnement et une décentralisation des pouvoirs pour une plus grande autonomie des provinces. Partisan d’une réforme constitutionnelle, c’est le premier collaborateur non-indépendantiste du collectif.', 7, true, 'Collaborateur', 40);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (7, 'Alexandre', 'Deschênes', '', 37, false, 'Lecteur', null);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (8, 'Adam', 'Fontaine-Métivier', 'Collaborateur à L’Agenda, diplômé en droit à l’Université de Sherbrooke. Né à La Sarre en Abitibi, il s’intéresse à la politique et son fonctionnement. Il utilise sa formation pour analyser les politiques provinciales et fédérales sous un angle plus neutre et plus soucieux des droits humains. Il apprécie particulièrement les institutions juridiques canadiennes pour leur neutralité et la charte canadienne des droits et libertés pour sa rigueur. Ses connaissances sur la constitution et le droit canadien sauront éclairer plusieurs lecteurs et auditeurs pour qui le monde juridique semble bien distant.', 10, true, 'Collaborateur', 90);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (9, 'Justin', 'Lachapelle', 'Collaborateur à l''Agenda, Justin Lachapelle étudie en génie logiciel à Polytechnique. Depuis quelques années, il s''intéresse à la politique québécoise et aux enjeux de société. Passionné de littérature, il aime lire les classiques pour se rappeler que le monde ne date pas d’hier, et qu’il y a certaines choses du passé qui méritent d’être préservées. Il cherche d’ailleurs à réhabiliter le conservatisme chez les jeunes.', 91, true, 'Collaborateur ', 69);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (10, 'Marie-Laurence', 'Desgagné', 'Collaboratrice à l’Agenda, conseillère au Comité national des jeunes du Parti Québécois et étudiante en droit et littérature à l’Université McGill. Née à Longueuil, mais originaire de la Beauce, elle s’intéresse particulièrement aux enjeux linguistiques et sociaux qui touchent la société québécoise. Les idéaux d’équité, de justice et d’émancipation collective guident tant ses opinions que ses actions, et la poussent à militer activement pour l’indépendance de l’État québécois. Pour elle, le dialogue est essentiel à la vie démocratique et au vivre-ensemble, et c’est dans cette optique qu’elle s’implique au sein de l’Agenda.', 14, true, 'Collaboratrice', 70);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (11, 'Jeremy ', 'Bélanger', 'Collaborateur de l’Agenda, ancien candidat de la CAQ en 2018 dans Rouyn-Noranda-Témiscamingue, ingénieur et gestionnaire chez Hydro-Québec et diplômé de l’École Polytechnique de Montréal en 2009. Né à La Sarre en Abitibi, il est passionné de politique, d’énergie et du Québec. Il est intéressé par les nouveaux mouvements politiques populistes de gauche et de droite. Il est toujours d’attaque pour débattre des grands enjeux de notre temps.', 38, true, 'Collaborateur', 60);
INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (12, 'Dereck', 'Dumont', 'Collaborateur à L’Agenda, étudiant en Droit-Sciences de la vie à l’Université de Sherbrooke et passionné de politique. Originaire du Nord-du-Québec et ayant habité la région du Bas-St-Laurent, il s’intéresse aux normes juridiques et aux politiques publiques dans plusieurs domaines tels que le droit de la santé, le droit des aliments, la propriété intellectuelle, les biotechnologies et le droit des personnes. Il s’intéresse notamment au droit en tant qu’institution pouvant rendre le système de santé dans son ensemble plus efficace, accessible et performant. De plus, pour lui, le droit est un outil permettant une meilleure intégration des nouvelles technologies dans la société. ', 39, true, 'Collaborateur', 100);
