INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (18, 'Natasha ', 'Tremblay', 'Collaboratrice à l’Agenda, candidate du Parti conservateur du Canada en 2019 et étudiante à l''Université Concordia en littérature anglaise. Natasha est originaire de l''Île-Bizard, à Montréal. Au niveau provincial, elle a milité pour la CAQ en 2018. Elle est favorable à la décentralisation des pouvoirs et une éventuelle réforme constitutionnelle pour une plus grande autonomie des provinces. Ses priorités comprennent le progrès social et économique, la saine gestion des finances publiques, la justice pénale et le droit des victimes d’actes criminels, et les questions culturelles et linguistiques touchant la société québécoise.', 47, false, 'Ex-Collaboratrice', null);
