INSERT INTO lagenda_api.Authors (Id, FirstName, LastName, `Desc`, ImageId, Collaborator, Title, `Order`) VALUES (28, 'Nicolas', 'Signori', 'Développeur et administrateur du site L’Agenda.ca, étudiant en ingénierie logicielle à l''ÉTS. Natif de l’Abitibi, il a accepté gracieusement de programmer nos services web et de nous aider malgré notre statut d’étudiant. Apolitique, il s’implique pour donner un coup de pouce à un ami. Pour sa générosité nous tenions à lui faire un petit hommage. Si vous trouvez son travail intéressant, allez jeter un coup d’oeil au https://nsignori.ca/.', 8, true, 'Administrateur et développeur', 80);
